package templates

// TODO(vardhan): Include only the necessary headers for a particular
// mojom.
const GenerateHeaderFile = `
{{- define "GenerateHeaderFile" -}}
// This file was auto-generated by the C bindings generator.

#ifndef {{.HeaderGuard}}
#define {{.HeaderGuard}}

#include <assert.h>
#include <math.h>
#include <mojo/bindings/array.h>
#include <mojo/bindings/buffer.h>
#include <mojo/bindings/interface.h>
#include <mojo/bindings/internal/type_descriptor.h>
#include <mojo/bindings/map.h>
#include <mojo/bindings/string.h>
#include <mojo/bindings/validation.h>
#include <mojo/macros.h>
#include <mojo/system/handle.h>
#include <stdbool.h>
#include <stdint.h>

// Imports.
{{range $import := .Imports -}}
#include "{{$import}}"
{{end}}

MOJO_BEGIN_EXTERN_C

// Forward declarations for structs.
{{range $struct := .Structs -}}
struct {{$struct.Name}};
union {{$struct.Name}}Ptr {
  struct {{$struct.Name}}* ptr;
  uint64_t offset;
};
MOJO_STATIC_ASSERT(sizeof(union {{$struct.Name}}Ptr) == 8,
              "union {{$struct.Name}}Ptr must be 8 bytes");

{{end -}}

// Forward declarations for unions.
{{range $union := .Unions -}}
struct {{$union.Name}};
union {{$union.Name}}Ptr {
  struct {{$union.Name}}* ptr;
  uint64_t offset;
};
MOJO_STATIC_ASSERT(sizeof(union {{$union.Name}}Ptr) == 8,
             "union {{$union.Name}} must be 8 bytes");

{{end -}}

// Top level constants.
{{range $const := .Constants -}}
extern const {{$const.Type}} {{$const.Name}};
{{end}}

// Top level enums.
{{range $enum := .Enums -}}
{{template "GenerateEnum" $enum}}
{{end}}

// Union definitions.
{{range $union := .Unions -}}
{{template "GenerateUnion" $union}}
{{end}}

// Struct definitions.
{{range $struct := .Structs -}}
{{template "GenerateStructDeclarations" $struct}}
{{end}}

// Interface definitions.
{{range $interface := .Interfaces -}}
{{template "GenerateInterfaceDeclarations" $interface}}
{{end}}

// Type tables declarations for structs and unions.
{{template "GenerateTypeTableDeclarations" .TypeTable}}

MOJO_END_EXTERN_C

#endif  // {{.HeaderGuard}}
{{- end }}
`
