package templates

const GenerateModFile = `
{{- define "GenerateModFile" -}}
/// This file was auto-generated by the Rust bindings generator.

// Module definitions:
{{range $module := .Files -}}
pub mod {{$module}};
{{end}}

// Submodule definitions:
{{range $module := .Submodules -}}
pub mod {{$module}};
{{end}}
{{end}}
`
